<template><router-link v-if="to" :to="to" :class="itemClasses" @click="handleClick" :title="collapsed ? title : ''" ><div v-if="icon" class="relative flex items-center justify-center" :class="collapsed ? 'w-10 h-10' : 'w-8 h-8'" ><FontAwesomeIcon :icon="icon" :class="[ 'flex-shrink-0 transition-all duration-300 group-hover:scale-110', collapsed ? 'w-6 h-6' : 'w-5 h-5', active ? 'text-white drop-shadow-sm' : 'text-gray-600 group-hover:text-primary-600' ]" /><div v-if="active" class="absolute inset-0 bg-white/30 rounded-lg blur opacity-50 animate-pulse"></div></div><span v-show="!collapsed" class="truncate font-medium transition-all duration-300" :class="active ? 'text-white drop-shadow-sm' : 'text-gray-700 group-hover:text-primary-700'" >{{ title}} </span><div v-if="active && !collapsed" class="ml-auto flex items-center" ><div class="w-2 h-2 bg-white rounded-full shadow-sm animate-pulse"></div></div></router-link><div v-else :class="itemClasses" @click="handleClick" :title="collapsed ? title : ''"><div v-if="icon" class="relative flex items-center justify-center" :class="collapsed ? 'w-10 h-10' : 'w-8 h-8'" ><FontAwesomeIcon :icon="icon" :class="[ 'flex-shrink-0 transition-all duration-300 group-hover:scale-110', collapsed ? 'w-6 h-6' : 'w-5 h-5', active ? 'text-white drop-shadow-sm' : 'text-gray-600 group-hover:text-primary-600' ]" /><div v-if="active" class="absolute inset-0 bg-white/30 rounded-lg blur opacity-50 animate-pulse"></div></div><span v-show="!collapsed" class="truncate font-medium transition-all duration-300 flex-1" :class="active ? 'text-white drop-shadow-sm' : 'text-gray-700 group-hover:text-primary-700'" >{{ title}} </span><slot name="arrow" /></div></template><script setup lang="ts">import{ computed} from 'vue'import{ useSidebarStore} from '@/stores/sidebar'interface Props{ title: string icon?: string to?: string active?: boolean hasChildren?: boolean collapsed?: boolean}const props=withDefaults(defineProps<Props>(),{ active: false, hasChildren: false, collapsed: false,})const emit=defineEmits<{ click: []}>()const sidebarStore=useSidebarStore()const handleClick=()=>{ // Se a sidebar estÃ¡ colapsada, abrir ela if (props.collapsed){ sidebarStore.open()} // Emitir o evento click original emit('click')}const itemClasses=computed(()=>{ const paddingX=props.collapsed ? 'px-2 mx-1' : 'px-4' const justifyContent=props.collapsed ? 'justify-center' : 'justify-start' const paddingY=props.collapsed ? 'py-3' : 'py-3' const gapClass=props.icon ? 'gap-3' : 'gap-0' const borderRadius=props.collapsed ? 'rounded-2xl' : 'rounded-xl' const baseClasses=`group relative flex items-center ${gapClass} ${paddingX} ${paddingY} ${justifyContent} ${borderRadius} transition-all duration-300 cursor-pointer transform hover:translate-x-1 overflow-hidden focus:outline-none` if (props.active){ return `${baseClasses} bg-gradient-to-r from-primary-500 to-secondary-500 text-white shadow-lg hover:shadow-xl hover:from-primary-600 hover:to-secondary-600`} return `${baseClasses} text-gray-700 hover:bg-gradient-to-r hover:from-primary-50 hover:to-secondary-50 hover:text-primary-700 hover:shadow-md`})</script>