if="cliente" @click="verDetalhesCliente" variant="ghost" size="sm"><FontAwesomeIcon :icon="['fas', 'external-link-alt']" class="mr-1" />Ver Cliente </BaseButton></div><div v-if="cliente" class="grid grid-cols-1 sm:grid-cols-2 gap-4 text-sm"><div><p class="text-gray-600">CPF:</p><p class="font-medium">{{ formatarCpf(cliente.cpf)}}</p></div><div v-if="cliente.telefones && cliente.telefones.length >0"><p class="text-gray-600">Telefone:</p><p class="font-medium">{{ cliente.telefones?.[0]?.telefone ? formatarTelefone(cliente.telefones[0].telefone) : 'N/A'}}</p></div><div v-if="cliente.emailClientes && cliente.emailClientes.length >0" class="sm:col-span-2"><p class="text-gray-600">Email:</p><p class="font-medium">{{ cliente.emailClientes?.[0]?.email || 'N/A'}}</p></div></div><p v-else class="text-gray-500 text-sm">Cliente n√£o encontrado</p></div></div><div v-else class="text-center py-8"><FontAwesomeIcon :icon="['fas', 'exclamation-circle']" class="text-gray-400 text-3xl mb-2" /><p class="text-gray-500">Animal n√£o encontrado</p></div></BaseCard></div><div class="mt-6 w-full min-w-0"><BaseCard class="shadow-xl border-0"><div class="flex items-center gap-3 mb-4"><div class="w-12 h-12 bg-gradient-to-br from-green-400 to-emerald-500 rounded-xl flex items-center justify-center"><FontAwesomeIcon :icon="['fas', 'dollar-sign']" class="text-white text-xl" /></div><div class="flex-1"><h2 class="text-xl font-bold text-gray-800">Resumo Financeiro</h2><p class="text-gray-600">{{ temServicosAdicionais ? `${1 + servicosAdicionais.length} servi√ßos contratados` : '1 servi√ßo contratado'}} ‚Ä¢ Total: R${{ valorTotalGeral.toFixed(2).replace('.', ',')}} </p></div><div class="flex items-center gap-2"><BaseButton @click="abrirModalEditarServico" variant="secondary" size="sm"><FontAwesomeIcon :icon="['fas', 'edit']" class="mr-1" />Editar Servi√ßo </BaseButton><BaseButton @click="adicionarNovoServicoAdicional" variant="primary" size="sm"><FontAwesomeIcon :icon="['fas', 'plus']" class="mr-1" /